export default{
    mathods:{
        trimValue(varible) {

            this.seller[varible] = this.seller[varible].replace(/^\s+|\s+$/gm, '')
        },

        showModal() {
            this.$refs['my-modal'].show()
        },
        hideModal() {
            this.$refs['my-modal'].hide()

        },
        findPrice()
    {
      var payload={
        minRange:this.minRange,
        maxRange:this.maxRange
      }

      this.$store.dispatch('searchProductByPrice',payload);
    },
    }
}
