import { getsellerdetail } from "@/service/SellerProductService";

export default {
  state: {
    sellerdetails:[]
  },
  getters: {
    getSeller(state) {
      return state.seller;
    },
  },

  mutations: {
    setSeller(state, value) {
      state.seller = value;
    },
  },

  actions: {
    SELLER_BY_ID(context, sellerid) {
      getsellerdetail({
        sellerid,
        success: (response) => {
          console.log(response);
         this.commit("setSeller",response);
        },
        error: (e) => {
          // this.commit("setSeller", []);
          console.warn(e);
        },
      });
    },
  }}
