import { reactivateSellerAccount, deleteAndCreateSellerAccount } from '@/service/SellerAccountService'
export default {
    name: 'RegisterComponent',
    data() {
        return {

            seller: {
                name: '',
                emailId: '',
                contact: '',
                address: '',
                password: ''

            },
            alertMessage: "",
            dismissSecs: 10,
            dismissCountDown: 0,
            checkPassword: '',
            passwordErrorFlag: false,
            nameErrorFlag: false,
            nameErrorMessage:"",
            emailErrorMessage:"",
            contactErrorMessage:"",
            addressErrorMessage:"",
