//  import { mapGetters } from 'vuex';
 import axios from 'axios';

export default {
    name: 'AdminDashboardComponent',
    data(){
        return {
            isLoading: false,
            seller:[],
            activeSellersCount:0,
            disabledSellersCount:0,
            requestCount:0
        }
    },
    // computed:
    // {
    //     ...mapGetters({
    //         activeSellers:'getSellers',

    //     })
    // },
    created(){
        this.$store.dispatch('GET_ALL_SELLERS');
        axios.get(`http://10.30.1.2:8002/admin/get/requestlist`)
        .then((response)=>{
           console.log("service request success"+response.data.data);
           this.requestCount=response.data.data
        })
        .catch((err)=>{
           console.log(err);
        })
        // this.$store.dispatch('USER_LOGGED_IN')
        this.active()
    },
    methods: {
        updateLoader(){
            this.isLoading = false; //fix after service layer is integrated.
            setInterval(()=>{
                this.isLoading = true;
            }, 5000);

        },
        createSeller(){
            this.$router.push({path: `/admindashboard/create`})
        },
        active(){
            axios.get('http://10.30.1.2:8002/admin/seller/getAll')
            .then((response)=>{
               console.log("service call success for sellers"+response);
               this.seller=response.data.data;
            })
            .catch((err)=>{
               console.log(err)
            })
            // let seller=this.$store.state.sellerStore.sellers;
             for(let i=0;i<this.seller.length;i++)
             {
                if(this.seller[i].status=="enabled")
                {
                    this.activeSellersCount++;  
                }
                if(this.seller[i].status=="disabled")
                {
                    this.disabledSellersCount++;  
                }
             }
             console.log(this.seller.length);
        }

    }
}
