      var DISBALE_SELLER_API = `http://${this.$ipAdress}:8002/admin/status/`
      console.log(userId)
   axios.put(DISBALE_SELLER_API + userId, userId)
         .then((response)=>{
               success && success(response);
         })
         .catch((err)=>{
               error && error(err);
         })
}

